mapper.update:
UPDATE question_set
name = #{name}, description = #{description}, category = #{category}, update_time = NOW()
WHERE id = #{id}

mapper.select:
SELECT  FROM question_set
WHERE user_id = #{userId}
ORDER BY create_time DESC

SELECT  FROM question_set
WHERE category = #{category}
ORDER BY create_time DESC

SELECT  FROM question_set
WHERE user_id = #{userId}
AND category = #{category}
ORDER BY create_time DESC

SELECT  FROM question_set
WHERE user_id = #{userId}
AND name LIKE #{name}
ORDER BY create_time DESC

SELECT  FROM question_set
WHERE user_id = #{userId}

    AND category = #{category}


    AND name LIKE #{name}

ORDER BY create_time DESC

mapper.update:
UPDATE question_set
SET is_public = 1,
    publisher_id = #{publisherId},
    publish_time = #{publishTime}
WHERE id = #{id}

mapper.select:
SELECT , is_public, publisher_id, publish_time
FROM question_set
WHERE is_public = 1

    AND category = #{category}


    AND name LIKE #{name}

ORDER BY publish_time DESC
